(function(){document.addEventListener("alpine:init",()=>{Alpine.data("scrollButton",()=>({showButton:!1,isAtBottom:!1,updateScrollPosition(){const t=window.scrollY||document.documentElement.scrollTop,o=document.documentElement.scrollHeight,e=document.documentElement.clientHeight;this.showButton=t>100,this.isAtBottom=Math.ceil(t+e)>=o-50},scrollToPosition(){const t=this.isAtBottom?0:document.documentElement.scrollHeight;window.scrollTo({top:t,behavior:"smooth"})}}))})})();
